#ifdef ENABLE_COLLECTOR

WheelEventCollection::WheelEventCollection (QObject *a_target)
  : _target (a_target)
  , _mutex (QMutex::Recursive)
{
  /* reserve slots */
  _positions.reserve (128);

  /* setup flush timer */
  _timeout  = new QTimer;
  _timeout->setSingleShot (true);
  _timeout->setInterval (50);

  QObject::connect (_timeout, &QTimer::timeout,
                    [this] { flush(); });
}

WheelEventCollection::~WheelEventCollection()
{
  _timeout->stop();
  delete _timeout;
}

void WheelEventCollection::push (TestItem &&a_value)
{
  DEBUGINFO;
  QMutexLocker l (&_mutex);

  /* store */
  _positions.append (std::move (a_value));

  /* start timer */
  if (!_timeout->isActive())
    _timeout->start();
}

void WheelEventCollection::push (const TestItem &a_value)
{
  DEBUGINFO;
  QMutexLocker l (&_mutex);

  /* store */
  _positions.append (a_value);

  /* start timer */
  if (!_timeout->isActive())
    _timeout->start();
}

int WheelEventCollection::sum() const
{
  int result = 0;

  for (const TestItem &value : _positions)
    result += value.DELTA_VALUE.y();
  result /= _positions.size();

  return result;
}

void WheelEventCollection::flush()
{
  DEBUGINFO;
  QMutexLocker l (&_mutex);

  /* check */
  if (_positions.isEmpty())
    return;

  /* get sum and clear positions */
  int result  = sum();
  DEBUGINFO << "result:" << result << "from" << _positions.size() << "values";
  TestItem item = _positions.first();
  _positions.clear();

  /* construct fake event */
  QWheelEvent event(
    item.pos,
    QPointF(),
    QPoint(),
    QPoint (0, result),
    Qt::NoButton,
    Qt::NoModifier,
    item.phase,
    item.inverted,
    item.source
  );

  /* send */
  QCoreApplication::sendEvent (_target, &event);
}

#endif // ENABLE_COLLECTOR
